import React from 'react';
import ReactDOM from 'react-dom';

class Portfolio extends React.Component {
    about = [
        {
            title: "Github",
            description: "Put a description here eventually.",
            url: "https://github.com/hexxzn"
        },
        {
            title: "Portfolio",
            description: "Put a description here eventually.",
            url: "https://georgekilburg.com"
        }
    ]

    listAbout = this.about.map((item) =>
        <div className="portfolio-card">
            <div className="portfolio-card-title">{item.title}</div>
            <div className="portfolio-card-description">{item.description}</div>
            <div className="portfolio-card-link">{item.url}</div>
        </div>
    );

    render() {
        return (
            <div className="container">
                <div className="portfolio-container row">
                    <div className="portfolio-left col-sm">
                        {this.listAbout}
                    </div>
                    <div className="portfolio-right col-sm">
                        <h5 className="item-head">Project Info</h5>
                        <h1 className="item-title">Article Title</h1>
                        <h3 className="item-subtitle">Article Subtitle</h3>
                        <p className="item-description">"Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum."</p>
                    </div>
                </div>
            </div>
        );
    }
}

ReactDOM.render(<Portfolio />, document.getElementById('root'));
export default Portfolio;
